// Якірні посилання, для плавного скролу по блокам 
const anchorLinks = document.querySelectorAll('.anchor-link');

anchorLinks.forEach(link => {
    link.addEventListener('click', event => {
      
    event.preventDefault();
  
      const targetId = link.getAttribute('href').slice(1),
            targetElement = document.getElementById(targetId);
  
      targetElement.scrollIntoView({ behavior: 'smooth' });
    });
});

// Кнопка, яка відображатиме додатковий текст
const moreBtn = document.querySelectorAll('.element-home__btn');

moreBtn.forEach(button => {
  button.addEventListener('click', () => {
    const block = button.closest('.element-home__content');
    const text = block.querySelector('.hidden-text');
    
    text.classList.toggle('active');
  });
});


// Перевірка паролю при авторизації
const submitButton = document.getElementById('enter'),
        pswInput = document.getElementById('psw'),
      authMessage = document.querySelector(".auth__message");

submitButton.addEventListener('click', () => {

    if(pswInput.value === "123"){
        window.location.href = 'http://127.0.0.1:5500/index.html';
    } else {
        authMessage.textContent = "Упс, хибний пароль"
    }
});

pswInput.addEventListener('input', () => {
    authMessage.textContent = "";
});